import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';

function ToDoFormTest(props) {
  const [item, addNewItem] = useState('');

  const handleInputChange = event => {
    addNewItem({ ...item, [event.target.name]: event.target.value });
  }

  const handleSubmit = event => {
    event.preventDefault();
    event.target.reset();
    props.handleSubmit(item);
    addNewItem(item);
  };

  return (
    <>
    <div>

    </div>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>
            To Do Item
          <Form.Control type="text" placeholder="Item Details" onChange={handleInputChange} />
          </Form.Label>
          <Form.Label>
            Difficulty Rating
            <Form.Control type="range" defaultValue="1" type="range" min="1" max="5" name="difficulty" onChange={handleInputChange} />
          </Form.Label>
          <Form.Label>
            Assigned To
            <Form.Control type="text" placeholder="Assigned Name" />
          </Form.Label>
        </Form.Group>
        <Button variant="primary" >
          Submit
        </Button>
      </Form>
      <h3></h3>
      <form onSubmit={handleSubmit}>
        <label>
          <span>To Do Item</span>
          <input
            name="text"
            placeholder="Add To Do List Item"
            onChange={handleInputChange}
          />
        </label>
        <label>
          <span>Difficulty Rating</span>
          <input defaultValue="1" type="range" min="1" max="5" name="difficulty" onChange={handleInputChange} />
        </label>
        <label>
          <span>Assigned To</span>
          <input type="text" name="assignee" placeholder="Assigned To" onChange={handleInputChange} />
        </label>
        <button>Add Item</button>
      </form>
    </>
  );

}

export default ToDoFormTest;
