import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';

function ToDoFormTest(props) {
  const [item, addNewItem] = useState('');

  const handleInputChange = event => {
    addNewItem({ ...item, [event.target.name]: event.target.value });
  }

  const handleSubmit = event => {
    event.preventDefault();
    event.target.reset();
    props.handleSubmit(item);
    addNewItem(item);
  };

  return (
    <>
    <Form onSubmit={handleSubmit}>
  <Form.Group controlId="formBasicEmail">
    <Form.Label>Add Item</Form.Label>
    <Form.Control type="text" placeholder="Add To Do List Item" />
    <Form.Label>Assignee</Form.Label>
    <Form.Control type="text" placeholder="Assignee" />
    <Form.Label>Difficulty</Form.Label>
    <Form.Control type="range" />
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" placeholder="Password" />
  </Form.Group>
  <Form.Group controlId="formBasicCheckbox">
    <Form.Check type="checkbox" label="Check me out" />
  </Form.Group>
  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>
      <h3></h3>
      <form onSubmit={handleSubmit}>
        <label>
          <span>To Do Item</span>
          <input
            name="text"
            placeholder="Add To Do List Item"
            onChange={handleInputChange}
          />
        </label>
        <label>
          <span>Difficulty Rating</span>
          <input defaultValue="1" type="range" min="1" max="5" name="difficulty" onChange={handleInputChange} />
        </label>
        <label>
          <span>Assigned To</span>
          <input type="text" name="assignee" placeholder="Assigned To" onChange={handleInputChange} />
        </label>
        <button>Add Item</button>
      </form>
    </>
  );

}

export default ToDoFormTest;
