import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import FormLabel from 'react-bootstrap/FormLabel'
import FormG from 'react-bootstrap/FormG'
import Button from 'react-bootstrap/Button';

function ToDoFormTest(props) {
  const [item, addNewItem] = useState('');

  const handleInputChange = event => {
    addNewItem({ ...item, [event.target.name]: event.target.value });
  }

  const handleSubmit = event => {
    console.log(1)
    event.preventDefault();
    event.target.reset();
    props.handleSubmit(item);
    addNewItem(item);
  };

  return (
    <>
    <div>
      <h3>Add To Do Item</h3>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formBasicEmail">
          <FormLabel>
            To Do Item
          <FormControl name="text" placeholder="Item Details" onChange={handleInputChange} />
          </FormLabel>
          <FormLabel>
            Difficulty Rating
            <FormControl type="range" defaultValue="1" type="range" min="1" max="5" name="difficulty" onChange={handleInputChange} />
          </FormLabel>
          <FormLabel>
            Assigned To
            <FormControl type="text" placeholder="Assigned Name" />
          </FormLabel>
        </Form.Group>
        <Button type="submit" variant="primary" >
          Submit
        </Button>
      </Form>
    </div>
      {/* <h3></h3>
      <form onSubmit={handleSubmit}>
        <label>
          <span>To Do Item</span>
          <input
            name="text"
            placeholder="Add To Do List Item"
            onChange={handleInputChange}
          />
        </label>
        <label>
          <span>Difficulty Rating</span>
          <input defaultValue="1" type="range" min="1" max="5" name="difficulty" onChange={handleInputChange} />
        </label>
        <label>
          <span>Assigned To</span>
          <input type="text" name="assignee" placeholder="Assigned To" onChange={handleInputChange} />
        </label>
        <button>Add Item</button>
      </form> */}
    </>
  );

}

export default ToDoFormTest;
