function ToDoTest(props) {
  const [list, setList] = useState([]);
  const [isOpen, setUpdateForm] = useState(false);

  const addItem = (item) => {
    item._id = Math.random();
    item.complete = false;
    setList([...list, item]);
  }

  const toggleComplete = id => {

    let item = list.filter(item => item._id === id)[0] || {};

    if (item._id) {
      item.complete = !item.complete;
      let newList = list.map(listItem => listItem._id === item._id ? item : listItem);
      setList(newList);
    }
  };

  const deleteItem = id => {
    let item = list.filter(item => item._id === id)[0] || {};
    if (item._id) {
      let newList = list.filter(items => items !== item);
      setList(newList);
    }
  }

  const updateItem = async id => {
    if (!isOpen) {
      let formState = await !isOpen;
      setUpdateForm(formState);
    }
    let item = list.filter(item => item._id === id)[0] || {};
    // console.log(document.querySelector('#updateText'))
    document.querySelector('#updateText').value = item.text;
    document.querySelector('#updateAssignee').value = item.assignee;
    document.querySelector('#updateDifficulty').value = item.difficulty;
    document.querySelector('#updateId').value = item._id;
    // let index = list.indexOf(item);
    // item.text = 'edited';
    // let newList = list.splice(index, 1, item);
    // console.log(newList);
    // console.log(list);
    // setList(newList);
  }

  const handleUpdateForm = event => {
    event.preventDefault();
    let id = document.querySelector('#updateId').value;
    let item = list.filter(item => item._id == id)[0];
    let index = list.indexOf(item);
    item.text = document.querySelector('#updateText').value;
    item.assignee = document.querySelector('#updateAssignee').value;
    item.difficulty = document.querySelector('#updateDifficulty').value;

    let newList = list.splice(index, 1, item);
    console.log(newList)
  }

  const closeUpdateForm = event => {
    event.preventDefault()
    let formState = !isOpen;
    setUpdateForm(formState);
  }

  useEffect(() => {
    let list = [
      { _id: 1, complete: false, text: 'Clean the Kitchen', difficulty: 3, assignee: 'Person A' },
      { _id: 2, complete: false, text: 'Do the Laundry', difficulty: 2, assignee: 'Person A' },
      { _id: 3, complete: false, text: 'Walk the Dog', difficulty: 4, assignee: 'Person B' },
      { _id: 4, complete: true, text: 'Do Homework', difficulty: 3, assignee: 'Person C' },
      { _id: 5, complete: false, text: 'Take a Nap', difficulty: 1, assignee: 'Person B' },
    ];

    setList(list);
  }, []);

  return (
    <>
      <header>
        <h2>
          There are {list.filter(item => !item.complete).length} Items To Complete
        </h2>
      </header>

      <section className="todo">

        <div>
          <TodoForm handleSubmit={addItem} />
        </div>

        <div>
          <TodoList
            list={list}
            handleComplete={toggleComplete} handleDelete={deleteItem} handleUpdate={updateItem}
          />
        </div>
        <If condition={isOpen}>
          <Then>
            <div>
              <form onSubmit={handleUpdateForm}>
                <input id="updateId" type="number" hidden value="" />
                <label htmlFor="text">
                  Title
                  <input id="updateText" type="text" name="text" />
                </label>
                <label htmlFor="assignee">
                  Assignee
                  <input id="updateAssignee" type="text" name="assignee" />
                </label>
                <label htmlFor="difficulty">Difficulty Range
                <input id="updateDifficulty" defaultValue="1" type="range" min="1" max="5" name="difficulty" />
                </label>
                <label htmlFor="dueDate">
                  Due Date
                  <input type="date" name="dueDate" />
                </label>
                <button> Update </button>
                <button onClick={closeUpdateForm}>Close</button>
              </form>
            </div>
          </Then>
        </If>
      </section>
    </>
  );
}

export default ToDoTest;
